#By TeamsSix#Blog teamssix.comimport requests, sysrequests.packages.urllib3.disable_warnings()def fckeditor_check():	f = open('dir_fckeditor.txt')	dir_list = f.readlines()	f.close()	for i in dir_list:		if i != '':			i = i.replace('\n','')			url_new = url+i			try:				r = requests.get(url_new,headers=headers,verify=False,timeout=7)				fckeditor_print(r,url_new)			except KeyboardInterrupt:				sys.exit()			except:				print('[-] {} {}'.format('Connection Failed',url_new))				passdef fckeditor_print(r,i):	r_status = r.status_code	if r_status == 200:		print('\033[1;34;40m[+] {} {} {} \033[0m'.format(r_status,len(r.text),i,))	else:		print('[-] {} {}'.format(r_status,i))if __name__ == '__main__':	try:		url = sys.argv[1]	except:		print('python3 fckeditor_check.py url')		sys.exit()	headers = {'User-Agent':'Mozilla/5.0 (Windows NT 10.0; Win64; x64; rv:74.0) Gecko/20100101 Firefox/74.0'}	fckeditor_check()